(46, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage  July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:20', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-06-15 03:15:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:34', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:37', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-payfast-may-2024-update-needed', 'update', 'en_US', 'An important update is required for the Payfast Payment Gateway', '<strong>Please update to the latest version of the Payfast Payment Gateway for WooCommerce immediately to address a security vulnerability</strong>. This update is required to ensure that your transactions continue to process.', '{}', 'pending', 'woocommerce.com', '2024-06-14 03:13:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2024-06-14 03:14:22', NULL, 0, 'plain', '', 0, 1, 'info'),
(59, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2024-06-14 03:15:33', NULL, 0, 'plain', '', 0, 1, 'info'),
(60, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2024-06-15 03:14:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2024-06-15 03:14:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2024-06-15 03:15:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2024-06-16 03:15:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2024-06-17 03:16:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protect your WooCommerce Store with Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2024-06-17 03:16:19', NULL, 0, 'thumbnail', 'http://localhost/cloth_store/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(66, 'checkout_block_update_july_2024', 'marketing', 'en_US', 'UPDATE: The checkout block is getting an upgrade!', 'Were improving the checkout block in 2024, starting with WooCommerce 9.0, launching on June 18! The latest release includes streamlined order summary with sticky functionality, better address form layout, and other enhancements to boost conversion.', '{}', 'pending', 'woocommerce.com', '2024-06-26 01:01:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'storefront-customize', 'info', 'en_US', 'Design your store with Storefront ', 'Visit the Storefront settings page to start setup and customization of your shop.', '{}', 'unactioned', 'storefront', '2024-06-28 01:23:39', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'klaviyo_q4_2024', 'marketing', 'en_US', 'Need a marketing solution to help you sell more?', 'Over 13,000 brands use Klaviyo to grow faster. You can drive 46x ROI with their personalized email, SMS, forms, and product reviews. Start for free today.', '{}', 'pending', 'woocommerce.com', '2024-08-01 01:41:31', NULL, 0, 'plain', '', 0, 0, 'info');
